"use strict";

document.addEventListener("DOMContentLoaded", function () {
  var t = document.querySelector(".prices__inner");
  t.addEventListener("mouseover", function (t) {
    var e = t.target;

    if (e.classList.contains("mixin-btn")) {
      var _t = e.style.backgroundColor;
      e.style.border = "1px solid " + _t, e.style.color = _t;
    }
  }), t.addEventListener("mouseout", function (t) {
    var e = t.target;
    e.classList.contains("mixin-btn") && (e.style.border = "1px solid transparent", e.style.color = "var(--color-white)");
  });
});